const uploadToBlob = async (containerName, blobName, data) => {
  // Create a new BlobServiceClient
  const {
    BlobServiceClient
  } = require("@azure/storage-blob");

  const blobServiceClient = new BlobServiceClient(blobSasUrl); // Get a container client from the BlobServiceClient

  const containerClient = blobServiceClient.getContainerClient(containerName);
  const blockBlobClient = containerClient.getBlockBlobClient(blobName);
  await blockBlobClient.upload(data, data.length);
};

const downloadFromBlob = async (containerName, blobName) => {
  // Create a new BlobServiceClient
  const {
    BlobServiceClient
  } = require("@azure/storage-blob");

  const blobServiceClient = new BlobServiceClient(blobSasUrl); // Get a container client from the BlobServiceClient

  const containerClient = blobServiceClient.getContainerClient(containerName);
  const blockBlobClient = containerClient.getBlockBlobClient(blobName);
  const blobClient = containerClient.getBlobClient(blobName);
  const downloadBlockBlobResponse = await blobClient.download();
  downloaded = await blobToString(await downloadBlockBlobResponse.blobBody);
  return downloaded;

  async function blobToString(blob) {
    const fileReader = new FileReader();
    return new Promise((resolve, reject) => {
      fileReader.onloadend = ev => {
        resolve(ev.target.result);
      };

      fileReader.onerror = reject;
      fileReader.readAsText(blob);
    });
  }
};

const deleteBlob = async (containerName, blobName) => {
  // Create a new BlobServiceClient
  const {
    BlobServiceClient
  } = require("@azure/storage-blob");

  const blobServiceClient = new BlobServiceClient(blobSasUrl); // Get a container client from the BlobServiceClient

  const containerClient = blobServiceClient.getContainerClient(containerName);
  await containerClient.deleteBlob(blobName);
};

const checkForBlobs = async (containerName, blobName) => {
  const {
    BlobServiceClient
  } = require("@azure/storage-blob");

  const blobServiceClient = new BlobServiceClient(blobSasUrl); // Get a container client from the BlobServiceClient

  const containerClient = blobServiceClient.getContainerClient(containerName);
  let iter = containerClient.listBlobsFlat();

  for await (const blob of iter) {
    if (blob.name === blobName) {
      return true;
    }
  }

  return false;
};

